import React, { useEffect, useState } from 'react';
import axios from 'axios';
import DemoComponent from './DemoComponent';

function WelcomeContent() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    // Retrieve the authentication token from your authentication state or storage
    const authToken = localStorage.getItem('authToken');
    // Verify and decode the JWT token to extract the claims
    const decodedToken = jwt.decode(authToken);

    // Extract the role from the decoded token
    const userRole = decodedToken.role;
    console.log(userRole); // Output: the role extracted from the token
    // Make an API call to retrieve user information, including the role, using the authentication token
    axios.get('/api/user', {
      headers: {
        Authorization: `Bearer ${authToken}`
      }
    }).then(response => {
      const { role } = response.data;
      setIsLoggedIn(true);
      setUserRole(role);
    }).catch(error => {
      setIsLoggedIn(false);
      setUserRole('');
      console.error('Error retrieving user information:', error);
    });
  }, []);

  const renderContent = () => {
    if (!isLoggedIn) {
      return <p>Login to see protected content</p>;
    } else {
      if (userRole === 'USER') {
        return <p>Hello, User</p>;
      } else if (userRole === 'ADMIN') {
        return <DemoComponent />;
      }
    }
  };

  return (
    <div>
      <h1>Welcome Content</h1>
      {renderContent()}
    </div>
  );
}

export default WelcomeContent;
